var img, html;
RUR.ANIMATION_TIME = 300; // default is 120 ms

numbers = ["src/images/1.png",
             "src/images/2.png",
             "src/images/3.png",
             "src/images/4.png",
             "src/images/5.png"];

tile_sync = {
    name: "sync",
    images : numbers,
    selection_method: "sync"
    };

tile_ordered = {
    name: "ordered",
    images : numbers,
    selection_method: "ordered"
    };

tile_random = {
    name : "random",
    images : numbers,
    selection_method: "random"
    };

tile_cycle = {
    name : "stay",
    images : numbers,
    selection_method: "cycle stay"
    };

tile_remove = {
    name : "remove",
    images : numbers,
    selection_method: "cycle remove"
    };

RUR.add_new_thing(tile_sync);
RUR.add_new_thing(tile_ordered);
RUR.add_new_thing(tile_random);
RUR.add_new_thing(tile_cycle);
RUR.add_new_thing(tile_remove);

img = "<img src='src/images/play.png' style='background-color:darkblue'>"; // Actual html img tag will not show in World Info.
html = "Program paused. Click on " + img + " above to resume";

function draw_tiles(name, y, end) {
    for(var x=1; x<=6; x++){
        RUR.add_background_tile(name, x, y);
    }
    if (end) {
        return;
    }
    print_html(html, true);
    pause();
    print_html('', true);
}

draw_tiles("sync", 2);
draw_tiles("ordered", 4);
draw_tiles("random", 6);
draw_tiles("stay", 8);
draw_tiles("remove", 10, true);
